import React,{useState} from 'react'
import { Modal,InputGroup,Form,Col } from "react-bootstrap";
import { addCategories, updateCategories } from '../../../reduxs/actions';
import { useDispatch } from "react-redux";
// import { Formik,  Field } from 'formik';
import * as Yup from 'yup';
import { Formik, useFormik } from 'formik';

const AddEditCategoryModal=({isEdit, show, setModal,onHide, catgory }) => {

    const [category, setCategory] = useState(catgory);
    const dispatch = useDispatch();
    const onCategory = (e, name) => {
        let cat = category;
        cat[name] = e.target.value;       
        setCategory({...cat});
    }

    const onSubmit = () => {
        if (isEdit)
        {
            dispatch(updateCategories(category));

        }
        else {
            console.log("ADD EDIT object");
           dispatch(addCategories({ category: category.category, description: category.description }));
        }
        setModal(false);
    }

    const validate = values => {
        const errors = {};
        if (!values.name) {
          errors.name = 'Required';
        } else if (values.name.length > 2) {
          errors.firstName = 'Must be 2 characters or less';
        }

        if (!values.des) {
          errors.des = 'Required';
        } else if (values.des.length > 20) {
          errors.lastName = 'Must be 20 characters or less';
        }

        // if (!values.email) {
        //   errors.email = 'Required';
        // } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
        //   errors.email = 'Invalid email address';
        // }

        return errors;
      };

    const formik = useFormik({
        initialValues: {
            category: category.category,
            description: category.description,
        //   email: '',
        },
        validate,
        onSubmit: values => {
            // onSubmit();
          alert(JSON.stringify(values, null, 2));
        },
    });
    const CForm = ({
        handleSubmit,
        handleChange,
        handleBlur,
        values,
        errors,
    }) => (
        <form onSubmit={handleSubmit}>
            <Form.Group className="mb-3" controlId="formPlaintextEmail">
                <Form.Label column sm="2">
                    Name
    </Form.Label>
                <Col sm="10">
                    <Form.Control type="text" name="name" id="name" onBlur={handleBlur} onChange={handleChange} value={formik.category || ''} defaultValue="email@example.com" />
                </Col>
            </Form.Group>
            {formik.errors.name ? <div>{formik.errors.name}</div> : null}

            <Form.Group className="mb-3" controlId="formPlaintextPassword">
                <Form.Label column sm="2">
                    Description
    </Form.Label>
                <Col sm="10">
                    <Form.Control as="textarea" onBlur={handleBlur} id="des" name="des" onChange={handleChange} rows={3} value={formik.description || ''} placeholder="description" />
                </Col>
                {formik.errors.des ? <div>{formik.errors.des}</div> : null}

            </Form.Group>
        </form>
    );


    return (
        <form onSubmit={formik.handleSubmit}>
            <Modal
                show={show}
                onHide={onHide}
                aria-labelledby="example-modal-sizes-title-lg"
            >
                <Modal.Header closeButton>
                    <Modal.Title id="example-modal-sizes-title-lg">
                        Customer Delete
          </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Formik component={ CForm } />
                </Modal.Body>
                <Modal.Footer>
                    <div>
                        <button
                            type="button"
                            onClick={onHide}
                            className="btn btn-light btn-elevate"
                        >
                            Cancel
            </button>
                        <> </>
                        <button
                            type="submit"
                            // onClick={onSubmit}
                            className="btn btn-primary btn-elevate"
                        >
                            {isEdit ? 'Edit' : 'Add'}
                        </button>
                    </div>
                </Modal.Footer>
            </Modal>
        </form>
    )
}

export default AddEditCategoryModal;
