import { USER_REGISTERATION } from "../actionTypes";
const initialAuthState = {
    user: undefined,
    authToken: undefined,
  };

const  reducer = (state = initialAuthState, { type, payload }) => {
    switch (type) {
        case USER_REGISTERATION.USER_REGISTERATION_INIT:
            return { ...state, loading: true };
        case USER_REGISTERATION.USER_REGISTERATION_SUCCESS:
            return { ...state, authToken: payload.authToken, loading: false };
        case USER_REGISTERATION.USER_REGISTERATION_ERROR:
            return { ...state, error: payload, loading: false };
        default:
            return state;
    }
};

export default reducer;
