import React, { useEffect,useMemo } from 'react'
import {
    Card,
    CardBody,
    CardHeader,
    CardHeaderToolbar,
} from "../../../_metronic/_partials/controls";
import { useDispatch,useSelector } from 'react-redux';
import { getCategories } from '../../../reduxs/actions';
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, {
  PaginationProvider,
} from "react-bootstrap-table2-paginator";
import { Pagination } from "../../../_metronic/_partials/controls";

import { ProductsFilter } from "../ECommerce/pages/products/products-filter/ProductsFilter";
import { ProductsTable } from "../ECommerce/pages/products/products-table/ProductsTable";
import { ProductsGrouping } from "../ECommerce/pages/products/products-grouping/ProductsGrouping";
import { useProductsUIContext } from "../ECommerce/pages/products/ProductsUIContext";

const Category = (props) => {

    const dispatch = useDispatch();
    const categories = useSelector(
        ({ auth})=> ({
            categories:auth.categorylst
    })
    )
    useEffect(() => {
        dispatch(getCategories());
        console.log("object",categories)
     },[])

    useEffect(() => {
        // dispatch(getCategories());
        console.log("object", categories)
    }, [categories]);

    var selectRowProp = {
        mode: "checkbox",
        clickToSelect: true,
        bgColor: "rgb(238, 193, 213)" 
      };

    return (
        <Card>
        <CardHeader title="Category List">
          <CardHeaderToolbar>
            <button
              type="button"
              className="btn btn-primary"
            //   onClick={productsUIProps.newProductButtonClick}
            >
              New Category
            </button>
          </CardHeaderToolbar>
        </CardHeader>
            <CardBody>
            <BootstrapTable
  data={categories}
  selectRow={selectRowProp}
  striped
  hover
  condensed
  pagination
  insertRow
  deleteRow
  search
></BootstrapTable>
           {/* <ProductsFilter /> */}
          {/*productsUIProps.ids.length > 0 && (
            <>
              <ProductsGrouping />
            </>
          )}
          <ProductsTable /> */}
        </CardBody>
      </Card>    )

}

export default Category;
